import React from 'react';
import { BrowserRouter as Router, Route, Switch, useHistory } from 'react-router-dom';
import './style.css';  // Certifique-se de que o caminho está correto
import Temas from './Temas';  // Importa o novo componente Temas

function App() {
  return (
    <Router>
      <Switch>
        <Route path="/" exact>
          <Login />
        </Route>
        <Route path="/temas" component={Temas} />
      </Switch>
    </Router>
  );
}

function Login() {
  const history = useHistory();

  const handleLogin = () => {
    // Navega para a página de temas
    history.push('/temas');
  };

  return (
    <div className="container-login">
      <div className="logo-login">
        <img src={process.env.PUBLIC_URL + '/assets/logo-lea.png'} alt="Logo LEA" style={{ height: '180px', marginLeft: '20px' }} />
      </div>
      <div className="login">
        <h1 style={{ color: '#ab619e' }}>Login</h1>
        <input id="placeholder-text" type="text" placeholder="Usuário" />
        <input id="placeholder-text" type="password" placeholder="Senha" />
        <button onClick={handleLogin}>Entrar</button>
      </div>
    </div>
  );
}

export default App;
